<?php //Based on the menu from e107faq.org //http://e107faq.org/e107_files/public/misc/estats_menu.txt //Edited to work with this plugin (bbclone) // //Original Commented Text: // // This is a menu to display BBClone stats on e107 website // Requires BBClone // Updated 06-Mar-2005 ... tested for v0.617 and v0.7  unset($text); require(_ESTATS_DIR."var/access.php");  $totalvisits   = $access["stat"]["totalvisits"]; $totalcount    = $access["stat"]["totalcount"]; $visitorsmonth = $access["time"]["month"][date("n")-1]; $visitorstoday = $access["time"]["wday"][date("w")]; $wday          = $access["time"]["wday"];  for($week = 0; list(,$wdays) = each($wday); $week += $wdays);  $text = " Total Visits: $totalvisits <br /> Total Unique: $totalcount  <br /> This Month: $visitorsmonth  <br /> This Week: $week  <br /> Today: $visitorstoday <br /><br /> <a href='".e_PLUGIN."bbclone' rel='external'>Click Here for Details</a> "; $caption = "BBClone Site Stats"; $ns -> tablerender($caption, $text); ?>